mapper.select:
SELECT ID , NAME , HOST_NAME , IP_ADDRESS , INSERT_DATE , JDK_VERSION , STATUS FROM DEVICE
AND ID = #{id}
AND INSERT_DATE = DATE_FORMAT(#{insertDate}, '%Y-%m-%d')

mapper.update:
UPDATE DEVICE


        NAME = ${name}


        HOST_NAME = ${hostName}


        IP_ADDRESS = ${ipAddress}


        INSERT_DATE = ${insertDate}


        JDK_VERSION = ${jdkVersion}


        STATUS = ${status}




        AND ID = ${id}
